import { Component, OnInit } from '@angular/core';
import { MovieService } from '../movie.service';
import { AuthService } from '../api.service'; // Import your authentication service

@Component({
  selector: 'app-movie-list',
  templateUrl: './movie-list.component.html',
  styleUrls: ['./movie-list.component.css']
})
export class MovieListComponent implements OnInit {
  movies: any[] = [];

  constructor(
    private movieService: MovieService,
    private ApiService: AuthService // Inject your authentication service
  ) { }

  ngOnInit() {
    // After a successful login, the authentication service should store the JWT token.
    // You can access it from there.
    const userToken = this.authService.getToken(); // Replace with the actual method in your AuthService

    if (!userToken) {
      // Handle the case where the token is not available (e.g., user is not logged in)
      console.error('User is not authenticated.');
      return;
    }

    // Use the retrieved token to fetch movies
    this.movieService.getMovies(userToken).subscribe(data => {
      this.movies = data;
    });
  }
}
