import { Component, OnInit} from '@angular/core';
import { MovieService } from '../movie.service'
import { ApiService } from '../api.service'

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.css']
})
export class MovieComponent implements OnInit {
  movies: any[] = [];
  userToken: string | null = null; 

  constructor(
    private movieService: MovieService,
    private apiService: ApiService
  ) {}

  ngOnInit() {
    this.userToken = localStorage.getItem('accessToken');
    console.log('tokenInMovie', this.userToken)

    if (!this.userToken) {
      console.error('User is not authenticated.');
      return;
    }

    this.movieService.getMovies(this.userToken).subscribe(
      (data) => {
        this.movies = data;
      },
      (error) => {
        if (error.status === 401) {
          console.error('Unauthorized: Please log in again.');
        } else {
          console.error('An error occurred while fetching movies:', error);
        }
      }
    );
  }
}
<div class="movie-container">
  <h2 class="movie">Movie List</h2>
  <a routerLink="/register" class="btn btn-danger">Add Movie</a>
  <a routerLink="/register" class="btn btn-danger"> </a>
  <a routerLink="/register" class="btn btn-danger">Register</a>

  <div class="row">
    <ng-container *ngIf="movies.length > 0; else noMovies">
      <div class="col-md-4" *ngFor="let movie of movies">
        <div class="card mb-4">
          <!-- <img src="{{ movie.imgurl }}" class="card-img-top" alt="{{ movie.title }}"> -->
          <div class="card-body">
            <h5 class="card-title">{{ movie.title }}</h5>
            <p class="card-text"><strong>Synopsis</strong> {{ movie.synopsis }}</p>
            <p class="card-text"><strong>Trailer :</strong> {{ movie.trailerurl }}</p>
            <p class="card-text"><strong>Img Url:</strong> <span id="imgUrlLimit"></span></p>
            <p class="card-text"><strong>Rating:</strong> {{ movie.rating }}</p>
            <p class="card-text"><strong>Status:</strong> {{ movie.status }}</p>

          </div>
        </div>
      </div>
    </ng-container>
    <ng-template #noMovies>
      <p class="col-md-12">No movies available.</p>
    </ng-template>
  </div>
</div>
<script>
  document.addEventListener("DOMContentLoaded", function() {
  // Get the full imgurl text
  var fullImgUrl = "{{ movie.imgurl }}";

  // Define the maximum number of words you want to display
  var maxWords = 10;

  // Split the text into an array of words
  var words = fullImgUrl.split(' ');

  // Check if the number of words exceeds the maximum
  if (words.length > maxWords) {
      // Create a truncated version of the text
      var truncatedText = words.slice(0, maxWords).join(' ');

      // Add an ellipsis to indicate truncation
      truncatedText += '...';

      // Display the truncated text
      document.getElementById('imgUrlLimit').textContent = truncatedText;
  } else {
      // If the text doesn't exceed the maximum, display it as is
      document.getElementById('imgUrlLimit').textContent = fullImgUrl;
  }
});

</script>
