import { Component, OnInit} from '@angular/core';
import { MovieService } from '../movie.service'
import { ApiService } from '../api.service'

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.css']
})
export class MovieComponent implements OnInit {
  movies: any[] = [];
  userToken: string | null = null; 

  constructor(
    private movieService: MovieService,
    private apiService: ApiService
  ) {}

  ngOnInit() {
    this.userToken = localStorage.getItem('accessToken');
    console.log('tokenInMovie', this.userToken)

    if (!this.userToken) {
      console.error('User is not authenticated.');
      return;
    }

    this.movieService.getMovies(this.userToken).subscribe(
      (data) => {
        this.movies = data;
      },
      (error) => {
        if (error.status === 401) {
          console.error('Unauthorized: Please log in again.');
        } else {
          console.error('An error occurred while fetching movies:', error);
        }
      }
    );
  }
}
<div class="movie-container">
  <h2>Movie List</h2>
  <div class="row">
    <ng-container *ngIf="movies.length > 0; else noMovies">
      <div class="col-md-4" *ngFor="let movie of movies">
        <div class="card mb-4">
          <!-- <img src="{{ movie.imgurl }}" class="card-img-top" alt="{{ movie.title }}"> -->
          <div class="card-body">
            <h5 class="card-title">{{ movie.title }}</h5>
            <p class="card-text"><strong>Synopsis</strong> {{ movie.synopsis }}</p>
            <p class="card-text"><strong>Trailer :</strong> {{ movie.trailerurl }}</p>
            <p class="card-text"><strong>Img Url:</strong> <span class="truncate-text">{{ movie.imgurl }}</span></p>

            <p class="card-text"><strong>Rating:</strong> {{ movie.rating }}</p>
            <p class="card-text"><strong>Status:</strong> {{ movie.status }}</p>

          </div>
        </div>
      </div>
    </ng-container>
    <ng-template #noMovies>
      <p class="col-md-12">No movies available.</p>
    </ng-template>
  </div>
</div>
