import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-movies',
  templateUrl: './movies.component.html',
  styleUrls: ['./movies.component.css']
})
export class MoviesComponent implements OnInit {
  posts: any;

  constructor(private service: ApiService, private router: Router) {}

  ngOnInit() {
    const accessToken = localStorage.getItem('accessToken');

    if (!accessToken) {
      // Handle the case where the token is not present (e.g., the user is not logged in)
      alert('You are not authorized. Please log in.');
      this.router.navigate(['/login']);
      return;
    }

    // If the token is present, you can use it in your API requests
    this.service.getPosts().subscribe(
      (response) => {
        this.posts = response;
      },
      (error) => {
        if (error.status === 401) {
          alert('Unauthorized, please log in again.');
          this.router.navigate(['/login']);
        } else {
          console.error('Error:', error);
          alert('An error occurred. Please try again later.');
        }
      }
    );
  }
}
<div *ngFor="let post of posts" class="movie-details">
  <h2>{{ post.title }}</h2>
  <p><strong>Synopsis:</strong> {{ post.synopsis }}</p>
  <!-- Rest of the movie details -->
</div>
