import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from 'your-auth-service'; // Import your authentication service
import { MovieService } from '../movie.service'; // Import your movie service

@Component({
  selector: 'app-movie-detail',
  templateUrl: './movie-detail.component.html',
  styleUrls: ['./movie-detail.component.css'],
})
export class MovieDetailComponent implements OnInit {
  movieId: string | null = null; // Initialize movieId as null or with a default value

  constructor(
    private route: ActivatedRoute,
    private authService: AuthService,
    private movieService: MovieService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.movieId = this.route.snapshot.paramMap.get('id');
  }

  bookmarkMovie() {
    const token = this.authService.getToken(); // Assuming you have a method to get the JWT token

    if (!token) {
      // Handle unauthorized access, e.g., redirect to login
      this.router.navigate(['/login']);
      return;
    }

    if (this.movieId) {
      this.movieService.bookmarkMovie(this.movieId, token).subscribe(
        (response) => {
          console.log('Movie bookmarked successfully!', response);
          // Handle success, e.g., show a success message
          this.router.navigate(['/movies']); // Redirect to /movies on success
        },
        (error) => {
          console.error('Error bookmarking movie:', error);
          // Handle error, e.g., show an error message
        }
      );
    }
  }
}
